import React, { useState, useEffect } from 'react';
import { View, StyleSheet, Alert, ScrollView } from 'react-native';
import { Text, Button, Card, FAB, List, Chip } from 'react-native-paper';
import * as Location from 'expo-location';
import { Camera } from 'expo-camera';

export default function AttendanceScreen() {
  const [location, setLocation] = useState(null);
  const [students, setStudents] = useState([
    { id: 1, name: 'John Doe', status: null, rollNumber: '001' },
    { id: 2, name: 'Jane Smith', status: null, rollNumber: '002' },
    { id: 3, name: 'Mike Johnson', status: null, rollNumber: '003' },
    { id: 4, name: 'Sarah Wilson', status: null, rollNumber: '004' },
  ]);
  const [cameraPermission, setCameraPermission] = useState(null);

  useEffect(() => {
    getLocationPermission();
    getCameraPermission();
  }, []);

  const getLocationPermission = async () => {
    let { status } = await Location.requestForegroundPermissionsAsync();
    if (status !== 'granted') {
      Alert.alert('Permission Denied', 'Location permission is required for attendance tracking');
      return;
    }

    let location = await Location.getCurrentPositionAsync({});
    setLocation(location);
  };

  const getCameraPermission = async () => {
    const { status } = await Camera.requestCameraPermissionsAsync();
    setCameraPermission(status === 'granted');
  };

  const markAttendance = (studentId, status) => {
    setStudents(prev => prev.map(student => 
      student.id === studentId ? { ...student, status } : student
    ));
  };

  const saveAttendance = () => {
    const attendanceData = {
      date: new Date().toISOString().split('T')[0],
      location: location ? {
        latitude: location.coords.latitude,
        longitude: location.coords.longitude
      } : null,
      students: students.map(student => ({
        id: student.id,
        name: student.name,
        rollNumber: student.rollNumber,
        status: student.status
      }))
    };

    // Here you would typically save to a database
    console.log('Attendance Data:', attendanceData);
    Alert.alert('Success', 'Attendance saved successfully!');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'present': return '#4CAF50';
      case 'absent': return '#F44336';
      case 'late': return '#FF9800';
      default: return '#9E9E9E';
    }
  };

  return (
    <View style={styles.container}>
      <ScrollView>
        <Card style={styles.card}>
          <Card.Content>
            <Text variant="headlineSmall">Today's Attendance</Text>
            <Text variant="bodyMedium">
              Date: {new Date().toLocaleDateString()}
            </Text>
            {location && (
              <Text variant="bodySmall" style={styles.locationText}>
                Location: {location.coords.latitude.toFixed(4)}, {location.coords.longitude.toFixed(4)}
              </Text>
            )}
          </Card.Content>
        </Card>

        {students.map((student) => (
          <Card key={student.id} style={styles.studentCard}>
            <Card.Content>
              <View style={styles.studentHeader}>
                <View>
                  <Text variant="titleMedium">{student.name}</Text>
                  <Text variant="bodySmall">Roll No: {student.rollNumber}</Text>
                </View>
                <Chip 
                  mode="outlined" 
                  style={[styles.statusChip, { borderColor: getStatusColor(student.status) }]}
                  textStyle={{ color: getStatusColor(student.status) }}
                >
                  {student.status || 'Not Marked'}
                </Chip>
              </View>
              
              <View style={styles.buttonRow}>
                <Button 
                  mode="contained" 
                  buttonColor="#4CAF50"
                  style={styles.attendanceButton}
                  onPress={() => markAttendance(student.id, 'present')}
                >
                  Present
                </Button>
                <Button 
                  mode="contained" 
                  buttonColor="#FF9800"
                  style={styles.attendanceButton}
                  onPress={() => markAttendance(student.id, 'late')}
                >
                  Late
                </Button>
                <Button 
                  mode="contained" 
                  buttonColor="#F44336"
                  style={styles.attendanceButton}
                  onPress={() => markAttendance(student.id, 'absent')}
                >
                  Absent
                </Button>
              </View>
            </Card.Content>
          </Card>
        ))}
      </ScrollView>

      <FAB
        icon="content-save"
        style={styles.fab}
        onPress={saveAttendance}
        label="Save Attendance"
        extended
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
    padding: 16,
  },
  card: {
    marginBottom: 16,
  },
  studentCard: {
    marginBottom: 8,
  },
  studentHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 12,
  },
  statusChip: {
    borderWidth: 1,
  },
  buttonRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    gap: 8,
  },
  attendanceButton: {
    flex: 1,
  },
  locationText: {
    marginTop: 4,
    color: '#666',
  },
  fab: {
    position: 'absolute',
    margin: 16,
    right: 0,
    bottom: 0,
  },
});
