import React, { useState } from 'react';
import { View, StyleSheet, ScrollView } from 'react-native';
import { Text, Card, FAB, List, Searchbar, IconButton, Button } from 'react-native-paper';

export default function StudentsScreen() {
  const [searchQuery, setSearchQuery] = useState('');
  const [students, setStudents] = useState([
    { 
      id: 1, 
      name: 'John Doe', 
      rollNumber: '001', 
      class: '10th Grade',
      phone: '+1234567890',
      address: 'Rural Village A',
      guardianName: 'Robert Doe'
    },
    { 
      id: 2, 
      name: 'Jane Smith', 
      rollNumber: '002', 
      class: '10th Grade',
      phone: '+1234567891',
      address: 'Rural Village B',
      guardianName: 'Mary Smith'
    },
    { 
      id: 3, 
      name: 'Mike Johnson', 
      rollNumber: '003', 
      class: '9th Grade',
      phone: '+1234567892',
      address: 'Rural Village A',
      guardianName: 'David Johnson'
    },
    { 
      id: 4, 
      name: 'Sarah Wilson', 
      rollNumber: '004', 
      class: '9th Grade',
      phone: '+1234567893',
      address: 'Rural Village C',
      guardianName: 'Linda Wilson'
    },
  ]);

  const filteredStudents = students.filter(student =>
    student.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    student.rollNumber.includes(searchQuery) ||
    student.class.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const addStudent = () => {
    // This would typically open a form modal
    console.log('Add student functionality would be implemented here');
  };

  const editStudent = (studentId) => {
    // This would typically open an edit form modal
    console.log('Edit student:', studentId);
  };

  const deleteStudent = (studentId) => {
    setStudents(prev => prev.filter(student => student.id !== studentId));
  };

  return (
    <View style={styles.container}>
      <Searchbar
        placeholder="Search students..."
        onChangeText={setSearchQuery}
        value={searchQuery}
        style={styles.searchbar}
      />

      <ScrollView>
        <Card style={styles.summaryCard}>
          <Card.Content>
            <Text variant="headlineSmall">Student Summary</Text>
            <Text variant="bodyMedium">Total Students: {students.length}</Text>
            <Text variant="bodyMedium">10th Grade: {students.filter(s => s.class === '10th Grade').length}</Text>
            <Text variant="bodyMedium">9th Grade: {students.filter(s => s.class === '9th Grade').length}</Text>
          </Card.Content>
        </Card>

        {filteredStudents.map((student) => (
          <Card key={student.id} style={styles.studentCard}>
            <Card.Content>
              <View style={styles.studentHeader}>
                <View style={styles.studentInfo}>
                  <Text variant="titleMedium">{student.name}</Text>
                  <Text variant="bodyMedium">Roll No: {student.rollNumber}</Text>
                  <Text variant="bodySmall">Class: {student.class}</Text>
                </View>
                <View style={styles.actions}>
                  <IconButton 
                    icon="pencil" 
                    size={20}
                    onPress={() => editStudent(student.id)}
                  />
                  <IconButton 
                    icon="delete" 
                    size={20}
                    iconColor="#F44336"
                    onPress={() => deleteStudent(student.id)}
                  />
                </View>
              </View>

              <List.Section style={styles.detailsList}>
                <List.Item
                  title="Guardian"
                  description={student.guardianName}
                  left={props => <List.Icon {...props} icon="account" />}
                />
                <List.Item
                  title="Phone"
                  description={student.phone}
                  left={props => <List.Icon {...props} icon="phone" />}
                />
                <List.Item
                  title="Address"
                  description={student.address}
                  left={props => <List.Icon {...props} icon="map-marker" />}
                />
              </List.Section>
            </Card.Content>
          </Card>
        ))}

        {filteredStudents.length === 0 && (
          <Card style={styles.emptyCard}>
            <Card.Content>
              <Text variant="bodyLarge" style={styles.emptyText}>
                No students found
              </Text>
              <Text variant="bodyMedium" style={styles.emptySubtext}>
                Try adjusting your search or add a new student
              </Text>
            </Card.Content>
          </Card>
        )}
      </ScrollView>

      <FAB
        icon="plus"
        style={styles.fab}
        onPress={addStudent}
        label="Add Student"
        extended
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
    padding: 16,
  },
  searchbar: {
    marginBottom: 16,
  },
  summaryCard: {
    marginBottom: 16,
  },
  studentCard: {
    marginBottom: 12,
  },
  studentHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
  },
  studentInfo: {
    flex: 1,
  },
  actions: {
    flexDirection: 'row',
  },
  detailsList: {
    marginTop: 8,
  },
  emptyCard: {
    marginTop: 32,
    alignItems: 'center',
  },
  emptyText: {
    textAlign: 'center',
    marginBottom: 8,
  },
  emptySubtext: {
    textAlign: 'center',
    color: '#666',
  },
  fab: {
    position: 'absolute',
    margin: 16,
    right: 0,
    bottom: 0,
  },
});
